CREATE TABLE IF NOT EXISTS `website` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增ID',
  `title` varchar(255) NOT NULL COMMENT '标题',
  `url` varchar(255) NOT NULL COMMENT '网址',
  `category_name` varchar(255) NOT NULL COMMENT '大分类',
  `sub_category_name` varchar(255) NOT NULL COMMENT '子分类',
  `is_show_menu` tinyint(4) NOT NULL DEFAULT '0' COMMENT '菜单显示 0: 否 1: 是',
  `menu_position` int(11) NOT NULL DEFAULT '1' COMMENT '菜单位置 1: 左边 2: 右边',
  `display_order` int(11) NOT NULL DEFAULT '0' COMMENT '排序',
  `class` varchar(50) NOT NULL DEFAULT 'btn-default' COMMENT '类',
  `style` varchar(50) NOT NULL DEFAULT 'height: 10000px;' COMMENT '样式',
  `iframe` varchar(50) DEFAULT NULL COMMENT 'iframe',
  `description` varchar(1000) DEFAULT NULL COMMENT '描述',
  `visit_count` int(11) NOT NULL DEFAULT '0' COMMENT '访问数',
  `remark` varchar(255) DEFAULT NULL COMMENT '备注',
  `created_at` datetime NOT NULL COMMENT '创建时间',
  `updated_at` datetime DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `index_url` (`url`)
) ENGINE=InnoDB AUTO_INCREMENT=61 DEFAULT CHARSET=utf8 COMMENT='网站';

